import { login, logout, signup } from './login';
import { updateSettings, tourSettings, createReview } from './updateSettings';
import { bookTour } from './stripe';
import { showAlert } from './alert';

//DOM ELEMENTS
const logoutBtn = document.getElementById('logout-btn');
const loginForm = document.querySelector('.form--login');
const signupForm = document.querySelector('.form--signup');
const userDataForm = document.querySelector('.form-user-data');
const userPasswordForm = document.querySelector('.form-user-password');
const createTourForm = document.querySelector('.form-create-tour');
const updateTourForm = document.querySelector('.form-update-tour');
const createReviewForm = document.querySelector('.form-tour-review');
const bookBtn = document.getElementById('book-tour');

if (loginForm) {
  loginForm.addEventListener('submit', (e) => {
    e.preventDefault();
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;
    login(email, password);
  });
}

if (signupForm) {
  signupForm.addEventListener('submit', (e) => {
    e.preventDefault();
    const name = document.getElementById('name').value;
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;
    const passwordConfirm = document.getElementById('password-confirm').value;
    signup(name, email, password, passwordConfirm);
  });
}

if (logoutBtn) {
  logoutBtn.addEventListener('click', logout);
}

if (userDataForm) {
  userDataForm.addEventListener('submit', (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append('name', document.getElementById('name').value);
    form.append('email', document.getElementById('email').value);
    form.append('photo', document.getElementById('photo').files[0]);

    updateSettings(form, 'data');
  });
}
if (userPasswordForm) {
  userPasswordForm.addEventListener('submit', async (e) => {
    e.preventDefault();
    document.querySelector('.btn--save-password').textContent = 'Updating...';
    const passwordCurrent = document.getElementById('password-current').value;
    const password = document.getElementById('password').value;
    const passwordConfirm = document.getElementById('password-confirm').value;
    await updateSettings(
      { passwordCurrent, password, passwordConfirm },
      'password'
    );
    document.getElementById('password-current').value = '';
    document.getElementById('password').value = '';
    document.getElementById('password-confirm').value = '';
    document.querySelector('.btn--save-password').textContent = 'Save Password';
  });
}

if (bookBtn) {
  bookBtn.addEventListener('click', (e) => {
    e.target.textContent = 'Processing...';
    const { tourId } = e.target.dataset; //tour-id => tourId(js ka jadu) and destructuring
    bookTour(tourId);
  });
}

const alertMessage = document.querySelector('body').dataset.alert;
if (!!alertMessage) showAlert('success', alertMessage, 15);

if (createTourForm) {
  createTourForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const form = new FormData();
    form.append('name', document.getElementById('name').value);
    form.append('startDates', [document.getElementById('startDates').value]);
    form.append('startLocation', {
      description: document.getElementById('locationDescription').value,
      address: document.getElementById('locationaddress').value,
    });
    form.append('maxGroupSize', document.getElementById('maxGroupSize').value);
    form.append('price', document.getElementById('price').value);
    form.append('duration', document.getElementById('duration').value);
    form.append('imageCover', document.getElementById('imageCover').files[0]);

    form.append('images', document.getElementById('images').files[0]);
    form.append('images', document.getElementById('images').files[1]);
    form.append('images', document.getElementById('images').files[2]);

    form.append('summary', document.getElementById('summary').value);
    form.append('description', document.getElementById('description').value);
    tourSettings(form, 'create');
  });
}

if (updateTourForm) {
  const date = new Date(document.getElementById('hidden-date').innerHTML);
  document.getElementById('startDates').defaultValue = date
    .toISOString()
    .slice(0, date.toISOString().indexOf('T'));

  updateTourForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const form = new FormData();
    form.append('name', document.getElementById('name').value);
    form.append('startDates', [document.getElementById('startDates').value]);
    form.append('maxGroupSize', document.getElementById('maxGroupSize').value);
    form.append('price', document.getElementById('price').value);
    form.append('duration', document.getElementById('duration').value);
    form.append('startLocation', {
      description: document.getElementById('locationDescription').value,
      address: document.getElementById('locationaddress').value,
    });
    if (document.getElementById('imageCover').files[0]) {
      form.append('imageCover', document.getElementById('imageCover').files[0]);
    }
    if (document.getElementById('images').files[0]) {
      form.append('images', document.getElementById('images').files[0]);
      form.append('images', document.getElementById('images').files[1]);
      form.append('images', document.getElementById('images').files[2]);
    }
    form.append('summary', document.getElementById('summary').value);
    form.append('description', document.getElementById('description').value);
    const tourId = document.getElementById('tourId').value;

    const isUpdate = confirm('Do you really want to update the tour ?');
    if (isUpdate) {
      tourSettings(form, 'update', tourId);
    }
  });
}

if (createReviewForm) {
  createReviewForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const rating = document.getElementById('rating').value;
    const review = document.getElementById('review').value;
    const id = document.getElementById('tourId').value;

    createReview({ rating, review }, id);
  });
}
